/* eslint-disable jsx-a11y/alt-text */
import React from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonItem, IonButton, IonLabel, IonGrid, IonRow, IonCol, IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonCardContent, IonIcon } from '@ionic/react';
import './Tab1.css';
import axios from 'axios';
import { addCircle } from 'ionicons/icons';

const URL = 'http://paintballinventory-env.eba-yah5svs5.us-east-2.elasticbeanstalk.com/api/guns';


const fetchGuns = () => {

  return axios({
    url: URL,
    method: 'get'
  }).then(response => {

    console.log(response);
    return response.data;
  })
};

const Guns: React.FC = () => {

    const [guns, setGuns] = React.useState([]);
    const [showModal, setShowModal] = React.useState(false);

  React.useEffect(() => {

    fetchGuns().then(data => setGuns(data));

  }, []);


let itemsToRender;
if (guns) {
  itemsToRender = guns.map(a=> {
    return <IonCol><IonCard>
            <img src={a['image']} />
          <IonCardHeader>
            <IonCardSubtitle>{a['date']}</IonCardSubtitle>
            <IonCardTitle>{a['name']}</IonCardTitle>
          </IonCardHeader>
          <IonCardContent>
          <IonButton href={'guns/'+ a['gun_id']} color="primary" slot="end">View Details</IonButton>
          <IonButton href={'guns/'+ a['gun_id']} color="primary" slot="end">Add Activity</IonButton>
          </IonCardContent>
        </IonCard>
        </IonCol>
        ;
  });
}
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>All Guns</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">

         <IonGrid>
         <IonRow>
         {itemsToRender}
         </IonRow>
         <IonRow>
        <IonCol>

        <IonCardHeader>
            <IonCardTitle>Add Gun</IonCardTitle>
          </IonCardHeader>
          <IonCardContent>
          <IonButton onClick={() => setShowModal(true)} color="primary" slot="end"><IonIcon slot="start" icon={addCircle} />Add</IonButton>
           </IonCardContent>
        </IonCol>


      </IonRow>
      </IonGrid>
      <IonModal isOpen={showModal} cssClass='my-custom-class'>
        <p>This is modal content</p>
        <IonButton onClick={() => setShowModal(false)}>Close Modal</IonButton>
      </IonModal>

      </IonContent>

    </IonPage>
  );
};


export default Guns;
